mapper.select:
SELECT * FROM (
	SELECT rownum AS r_num, a.* FROM (
		SELECT * FROM cmty_t
		
		
			c_status = 0 AND
		
		c_category = 1
		
		ORDER BY c_idx DESC
		) a
) WHERE r_num BETWEEN #{begin} AND #{end}

mapper.update:
UPDATE cmty_t
SET c_status = 1
WHERE c_idx = #{c_idx}

UPDATE cmty_t
SET c_title=#{c_title},
	c_content=#{c_content}

	,c_img=#{c_img}

WHERE c_idx=#{c_idx}
